<?phpconst ROOT = __DIR__;$config = file_get_contents(__DIR__ . "/config.json");$config = json_decode($config, true);$hostname = $config['database']['hostname'];$port = $config['database']['port'];$database = $config['database']['database'];$user = $config['database']['user'];$password = $config['database']['password'];function controller ($name) {    require_once(__DIR__ . "/controllers/$name.php");}function model ($name) {    require_once(__DIR__ . "/models/$name.php");}function view ($name) {    require_once(__DIR__ . "/views/$name.php");}function partial ($name) {    require_once(__DIR__ . "/html_partials/$name.html.php");}function import_php ($name) {    require_once(__DIR__ . "/php/$name.php");}function redirect($page) {    header('Location: ' .$page);    die();}function bdd_connection() {    global $hostname, $port, $database, $user, $password;    return new PDO("mysql:host=$hostname;port=$port;dbname=$database", $user, $password, array(PDO::MYSQL_ATTR_INIT_COMMAND => 'SET NAMES utf8'));}function printInfo($sMsg) {    echo '<div class="alert info"><i class="uil uil-times alert__close" id="nav-close"></i><strong>Info : </strong> '.$sMsg.'</div>';    // echo '<div class="alert alert-info"><strong>Info : </strong>'.$sMsg.'</div>';}function printSuccess($sMsg) {    echo '<div class="alert success"><i class="uil uil-times alert__close" id="nav-close"></i><strong>Succès : </strong> '.$sMsg.'</div>';    // echo '<div class="alert alert-success"><strong>Succès : </strong>'.$sMsg.'</div>';}function printWarning($sMsg) {    echo '<div class="alert warning"><i class="uil uil-times alert__close" id="nav-close"></i><strong>Attention : </strong> '.$sMsg.'</div>';    // echo '<div class="alert alert-warning"><strong>Attention : </strong>'.$sMsg.'</div>';}function printError($sMsg) {    echo '<div class="alert"><i class="uil uil-times alert__close" id="nav-close"></i><strong>Erreur : </strong> '.$sMsg.'</div>';    // echo '<div class="alert alert-danger"><strong>Erreur : </strong>'.$sMsg.'</div>';}function filter_string_polyfill(string $string): string{    $str = preg_replace('/\x00|<[^>]*>?/', '', $string);    return str_replace(["'", '"'], ['&#39;', '&#34;'], $str);}// Get functionsfunction getString($sN) {    $str = filter_input(INPUT_GET, $sN, FILTER_UNSAFE_RAW, FILTER_NULL_ON_FAILURE|FILTER_FLAG_NO_ENCODE_QUOTES);    return filter_string_polyfill($str);}function get($sN) {    return filter_input(INPUT_GET, $sN,FILTER_DEFAULT, FILTER_NULL_ON_FAILURE|FILTER_FLAG_NO_ENCODE_QUOTES);}function getFloat($sN) {    return filter_input(INPUT_GET, $sN,FILTER_VALIDATE_FLOAT, FILTER_NULL_ON_FAILURE);}function getInt($sN) {    return filter_input(INPUT_GET, $sN,FILTER_VALIDATE_INT, FILTER_NULL_ON_FAILURE);}function getArray($sN) {    return filter_input(INPUT_GET, $sN, FILTER_DEFAULT, FILTER_REQUIRE_ARRAY);}// Post functionsfunction postString($sN) {    $str = filter_input(INPUT_POST, $sN, FILTER_UNSAFE_RAW, FILTER_NULL_ON_FAILURE|FILTER_FLAG_NO_ENCODE_QUOTES);    return filter_string_polyfill($str);}function post($sN) {    return filter_input(INPUT_POST, $sN,FILTER_DEFAULT,FILTER_NULL_ON_FAILURE|FILTER_FLAG_NO_ENCODE_QUOTES);}function postInt($sN) {    return filter_input(INPUT_POST, $sN,FILTER_VALIDATE_INT,FILTER_NULL_ON_FAILURE);}function postFloat($sN) {    return filter_input(INPUT_POST, $sN,FILTER_VALIDATE_FLOAT, FILTER_NULL_ON_FAILURE);}function postArray($sN) {    return filter_input(INPUT_POST, $sN, FILTER_DEFAULT, FILTER_REQUIRE_ARRAY);}// Other functionsfunction getIP() {    return filter_input(INPUT_SERVER,'REMOTE_ADDR');}function printDay($sTime=null) {    if ($sTime === null) {        $sTime=time();    }    $aNameMonths[1] = 'janvier';    $aNameMonths[2] = 'février';    $aNameMonths[3] = 'mars';    $aNameMonths[4] = 'avril';    $aNameMonths[5] = 'mai';    $aNameMonths[6] = 'juin';    $aNameMonths[7] = 'juillet';    $aNameMonths[8] = 'août';    $aNameMonths[9] = 'septembre';    $aNameMonths[10] = 'octobre';    $aNameMonths[11] = 'novembre';    $aNameMonths[12] = 'décembre';    $aDays[0] = 'dimanche';    $aDays[1] = 'lundi';    $aDays[2] = 'mardi';    $aDays[3] = 'mercredi';    $aDays[4] = 'jeudi';    $aDays[5] = 'vendredi';    $aDays[6] = 'samedi';    return date('h:i:s',$sTime).' le '.$aDays[date('w',$sTime)].', '.date('d',$sTime).' '.$aNameMonths[date('n',$sTime)].' '.date('Y',$sTime);}